package fiberoapi

import (
	"html/template"
	"strings"

	"github.com/gofiber/fiber/v2"
)

// DocConfig contains configuration for the documentation
type DocConfig struct {
	Title       string
	Description string
	Version     string
	DocsPath    string // Path where docs will be served, default: "/docs"
	JSONPath    string // Path where OpenAPI JSON will be served, default: "/openapi.json"
}

// DefaultDocConfig returns default documentation configuration
func DefaultDocConfig() DocConfig {
	return DocConfig{
		Title:       "API Documentation",
		Description: "API documentation generated by fiber-oapi",
		Version:     "1.0.0",
		DocsPath:    "/docs",
		JSONPath:    "/openapi.json",
	}
}

// SetupDocs configures documentation routes for the OApiApp
func (o *OApiApp) SetupDocs(config ...DocConfig) {
	cfg := DefaultDocConfig()
	if len(config) > 0 {
		cfg = config[0]
	}

	// Serve OpenAPI JSON specification
	o.Get(cfg.JSONPath, func(c *fiber.Ctx) error {
		spec := o.GenerateOpenAPISpec()

		// Override info section with config values
		if info, ok := spec["info"].(map[string]interface{}); ok {
			info["title"] = cfg.Title
			info["description"] = cfg.Description
			info["version"] = cfg.Version
		}

		c.Set("Content-Type", "application/json")
		return c.JSON(spec)
	})

	// Serve Swagger UI documentation
	o.Get(cfg.DocsPath, func(c *fiber.Ctx) error {
		// Generate HTML with embedded Swagger UI
		html := generateSwaggerHTML(cfg.JSONPath, cfg.Title)
		c.Set("Content-Type", "text/html")
		return c.SendString(html)
	})

	// Serve additional docs routes (with trailing slash)
	o.Get(cfg.DocsPath+"/", func(c *fiber.Ctx) error {
		return c.Redirect(cfg.DocsPath)
	})
}

// generateSwaggerHTML generates HTML page with embedded Swagger UI
func generateSwaggerHTML(jsonPath, title string) string {
	tmpl := `<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>{{.Title}}</title>
    <link rel="stylesheet" type="text/css" href="https://unpkg.com/swagger-ui-dist@5.10.3/swagger-ui.css" />
    <link rel="icon" type="image/png" href="data:image/png;base64," />
    <style>
        html {
            box-sizing: border-box;
            overflow: -moz-scrollbars-vertical;
            overflow-y: scroll;
        }
        *, *:before, *:after {
            box-sizing: inherit;
        }
        body {
            margin:0;
            background: #fafafa;
        }
    </style>
</head>
<body>
    <div id="swagger-ui"></div>
    <script src="https://unpkg.com/swagger-ui-dist@5.10.3/swagger-ui-bundle.js"></script>
    <script src="https://unpkg.com/swagger-ui-dist@5.10.3/swagger-ui-standalone-preset.js"></script>
    <script>
        window.onload = function() {
            const ui = SwaggerUIBundle({
                url: '{{.JSONPath}}',
                dom_id: '#swagger-ui',
                deepLinking: true,
                presets: [
                    SwaggerUIBundle.presets.apis,
                    SwaggerUIStandalonePreset
                ],
                plugins: [
                    SwaggerUIBundle.plugins.DownloadUrl
                ],
                layout: "StandaloneLayout",
                validatorUrl: null
            });
        };
    </script>
</body>
</html>`

	t := template.Must(template.New("swagger").Parse(tmpl))
	var buf strings.Builder
	t.Execute(&buf, struct {
		Title    string
		JSONPath string
	}{
		Title:    title,
		JSONPath: jsonPath,
	})
	return buf.String()
}
